// deno-lint-ignore-file
import {
  applyParamsToScript,
  Data,
  Script,
} from "https://deno.land/x/lucid@0.20.4/mod.ts";

export type AssetName = string;
export type Bool = boolean;
export type ByteArray = string;
export type Int = bigint;
export type ListAssetName = Array<AssetName>;
export type PolicyId = string;
export type ScriptHash = string;
export type Void = undefined;
export type CardanoTransactionOutputReference = {
  transactionId: ByteArray;
  outputIndex: Int;
};
export type SmttValidatorsRunRunDatum = { started: Bool };
export type SmttValidatorsTagKeyDatum = { keys: ListAssetName };

const definitions = {
  "AssetName": { "title": "AssetName", "dataType": "bytes" },
  "Bool": {
    "title": "Bool",
    "anyOf": [{
      "title": "False",
      "dataType": "constructor",
      "index": 0,
      "fields": [],
    }, {
      "title": "True",
      "dataType": "constructor",
      "index": 1,
      "fields": [],
    }],
  },
  "ByteArray": { "title": "ByteArray", "dataType": "bytes" },
  "Int": { "dataType": "integer" },
  "List$AssetName": {
    "dataType": "list",
    "items": { "$ref": "#/definitions/AssetName" },
  },
  "PolicyId": { "title": "PolicyId", "dataType": "bytes" },
  "ScriptHash": { "title": "ScriptHash", "dataType": "bytes" },
  "Void": {
    "title": "Unit",
    "anyOf": [{ "dataType": "constructor", "index": 0, "fields": [] }],
  },
  "cardano/transaction/OutputReference": {
    "title": "OutputReference",
    "description":
      "An `OutputReference` is a unique reference to an output on-chain. The `output_index`\n corresponds to the position in the output list of the transaction (identified by its id)\n that produced that output",
    "anyOf": [{
      "title": "OutputReference",
      "dataType": "constructor",
      "index": 0,
      "fields": [{
        "title": "transactionId",
        "$ref": "#/definitions/ByteArray",
      }, { "title": "outputIndex", "$ref": "#/definitions/Int" }],
    }],
  },
  "smtt/validators/run/RunDatum": {
    "title": "RunDatum",
    "anyOf": [{
      "title": "RunDatum",
      "dataType": "constructor",
      "index": 0,
      "fields": [{ "title": "started", "$ref": "#/definitions/Bool" }],
    }],
  },
  "smtt/validators/tag/KeyDatum": {
    "title": "KeyDatum",
    "anyOf": [{
      "title": "KeyDatum",
      "dataType": "constructor",
      "index": 0,
      "fields": [{ "title": "keys", "$ref": "#/definitions/List$AssetName" }],
    }],
  },
};

export interface StorageRunSpend {
  new (
    sttMint: PolicyId,
    tagMint: PolicyId,
    tagSpend: ScriptHash,
    _contractSpend: ScriptHash,
    splitThreshold: Int,
  ): Script;
  _d: SmttValidatorsRunRunDatum;
  _r: Void;
}

export const StorageRunSpend = Object.assign(
  function (
    sttMint: PolicyId,
    tagMint: PolicyId,
    tagSpend: ScriptHash,
    _contractSpend: ScriptHash,
    splitThreshold: Int,
  ) {
    return {
      type: "PlutusV3",
      script: applyParamsToScript(
        [sttMint, tagMint, tagSpend, _contractSpend, splitThreshold],
        "",
        {
          "shape": {
            "dataType": "list",
            "items": [
              { "$ref": "#/definitions/PolicyId" },
              { "$ref": "#/definitions/PolicyId" },
              { "$ref": "#/definitions/ScriptHash" },
              { "$ref": "#/definitions/ScriptHash" },
              { "$ref": "#/definitions/Int" },
            ],
          },
          definitions,
        } as any,
      ),
    };
  },
  {
    _d: {
      "shape": { "$ref": "#/definitions/smtt/validators/run/RunDatum" },
      definitions,
    },
  },
  { _r: { "shape": { "$ref": "#/definitions/Void" }, definitions } },
) as unknown as StorageRunSpend;

export interface StorageSttMint {
  new (utxo: CardanoTransactionOutputReference): Script;
  _r: Void;
}

export const StorageSttMint = Object.assign(
  function (utxo: CardanoTransactionOutputReference) {
    return {
      type: "PlutusV3",
      script: applyParamsToScript(
        [utxo],
        "",
        {
          "shape": {
            "dataType": "list",
            "items": [{
              "$ref": "#/definitions/cardano/transaction/OutputReference",
            }],
          },
          definitions,
        } as any,
      ),
    };
  },
  { _r: { "shape": { "$ref": "#/definitions/Void" }, definitions } },
) as unknown as StorageSttMint;

